import React, { Component } from "react";
import { API, graphqlOperation } from "aws-amplify";
import * as queries from "../src/graphql/queries";
const listFacility = props => (
  <>
    {console.log(props)}
    <p>{props.facilityType}</p>

    <div className="main">
      <div className="card">
        <div className="card-title">Gate 1</div>
        <div className="card-body">
          <table>
            <thead>
              <tr>
                <th>S/N</th>
                <th>Item Name</th>
                <th>Fee</th>
                <th>Quantity</th>
                <th>Amount</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>1</td>
                <td>Keke Napep</td>
                <td>20</td>
                <td>100</td>
                <td>2000</td>
              </tr>
              <tr>
                <td>1</td>
                <td>Cars</td>
                <td>20</td>
                <td>100</td>
                <td>2000</td>
              </tr>
              <tr>
                <td>1</td>
                <td>Truck/Lorry</td>
                <td>20</td>
                <td>100</td>
                <td>2000</td>
              </tr>
              <tr>
                <td>1</td>
                <td>Canter</td>
                <td>20</td>
                <td>100</td>
                <td>2000</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </>
);
listFacility.getInitialProps = async function({ query }) {
  let nextToken = null;
  let results = [];
  let errors;
  let raw = {};
  try {
    do {
      raw = await API.graphql(
        graphqlOperation(queries.listFacilitys, {
          filter: { facilityType: { eq: query.facilityType } },
          limit: 10000,
          nextToken: nextToken
        })
      );
      nextToken = raw.data.listFacilitys.nextToken;
      raw.data.listFacilitys.items.map(value => results.push(value));
    } while (nextToken);
  } catch (error) {
    errors = error;
  }
  return { data: results, facilityType: query.facilityType, errors: errors };
};
export default listFacility;
