import React, { Component } from "react";
import { API, graphqlOperation } from "aws-amplify";
import * as queries from "../src/graphql/queries";
import Link from "next/link";
const listFacility = props => (
  <>
    {console.log(props)}
    <p>{props.facilityType}</p>
    <div class="header">Unpaid Facility Charges for Shops This Month</div>

    <div class="main">
      <div class="card-list">
        <div class="card-title">200 Shops for preview</div>
        <div class="card-body">
          <table>
            <thead>
              <th>S/N</th>
              <th>Facility ID</th>
              <th>Owner Name</th>
              <th>Owner Contact</th>
              <th>Due Date</th>
            </thead>
            <tbody>
              {props.data.map((f, index) => (
                <Link
                  href={{
                    pathname: "/FacilityDetail"
                  }}
                >
                  <tr>
                    <td>{index + 1}</td>
                    <td>
                      <a>JUMM/A/0002</a>
                    </td>
                    <td>John Musa</td>
                    <td>08030100804</td>
                    <td>2019-12-12</td>
                  </tr>
                </Link>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </>
);
listFacility.getInitialProps = async function({ query }) {
  let nextToken = null;
  let results = [];
  let errors;
  let raw = {};
  try {
    do {
      raw = await API.graphql(
        graphqlOperation(queries.listFacilitys, {
          filter: { facilityType: { eq: query.facilityType } },
          limit: 10000,
          nextToken: nextToken
        })
      );
      nextToken = raw.data.listFacilitys.nextToken;
      raw.data.listFacilitys.items.map(value => results.push(value));
    } while (nextToken);
  } catch (error) {
    errors = error;
  }
  return { data: results, facilityType: query.facilityType, errors: errors };
};
export default listFacility;
